//(see description for more info)

//decoding the payload --------------------------------------------------------------

//pversion = version according to the source - max 255 - 0 when the payload is a test - 1 from MacBook
// 2 from raspberry, 3 fix position and time, 4 test with analogic input
pversion = 4;

ptime=msg.payload.received_at;//a char with the date and time
flag01 = msg.payload.uplink_message.decoded_payload.bytes[0];//expected value 01
temp1 = msg.payload.uplink_message.decoded_payload.bytes[1];//temp part 1
temp2 = msg.payload.uplink_message.decoded_payload.bytes[2];//temp part 2
flag02 = msg.payload.uplink_message.decoded_payload.bytes[3];//expected value 02
humidity = msg.payload.uplink_message.decoded_payload.bytes[4];//humidity
flag03 = msg.payload.uplink_message.decoded_payload.bytes[5];//expected value 03, not used
flag07 = msg.payload.uplink_message.decoded_payload.bytes[9];//expected value 07
alim1 = msg.payload.uplink_message.decoded_payload.bytes[10];//internal battery voltage byte 1
alim2 = msg.payload.uplink_message.decoded_payload.bytes[11];//internal battery voltage byte 2
flag08 = msg.payload.uplink_message.decoded_payload.bytes[12];//expected value 08 hex or 11 decimal
analog1 = msg.payload.uplink_message.decoded_payload.bytes[13];//analogic entry part 1
analog2 = msg.payload.uplink_message.decoded_payload.bytes[14];//analogic entry part 2
flag0F = msg.payload.uplink_message.decoded_payload.bytes[15];//expected value 0F hex or 14 decimal, not used
flag14 = msg.payload.uplink_message.decoded_payload.bytes[17];//expected value 14 hex or 20 decimal
press1 = msg.payload.uplink_message.decoded_payload.bytes[18];//pressure part 1
press2 = msg.payload.uplink_message.decoded_payload.bytes[19];//pressure part 2
press3 = msg.payload.uplink_message.decoded_payload.bytes[20];//pressure part 3
press4 = msg.payload.uplink_message.decoded_payload.bytes[21];//pressure part 4
flag18 = msg.payload.uplink_message.decoded_payload.bytes[22];//expected value 18 hex or 24 decimal, not used

if ((flag01 == 1) && (flag02 == 2) && (flag03 == 3) && (flag07 == 7) && (flag08 == 8)&& (flag0F == 15) && (flag14 == 20) && (flag18 == 24))  {
    ptemperature = (temp1*256 + temp2);//degre x 10, to be divided by 10
    phumidity = humidity;//HR
    pvoltage = (alim1*256 + alim2);//mV
    ppressure = Math.round((press1*16777216 + press2*65536 + press3*256 + press4)/1000);
    panalog = (analog1*256 + analog2);//mv

    //define the SQL fetch to be sent to Topic, réorganiser les variables car pas clair!
    var newMsg1 = {
    "topic":"INSERT INTO ELT2Piscine (version,temperature,humidity,voltage,pressure,analog,time)VALUES ("+"\'"+pversion+"\',\'"+ptemperature+"\',\'"+phumidity+"\',\'"+pvoltage+"\',\'"+ppressure+"\',\'"+panalog+"\',\'"+ptime+"\')"
        };

    //data for gage
    newMsg1.temperature = ptemperature/10;
    newMsg1.humidity = phumidity;
    newMsg1.voltage = pvoltage;
    newMsg1.pressure = ppressure;
    newMsg1.analog = panalog;
    var newMsg2 = {"payload": panalog/1000};
    var newMsg3 = {"payload": ptemperature/10};


    return [newMsg1, newMsg2, newMsg3];

} else {
    //do nothing if the payload is not correct
    node.error("From SQL decoder : the payload is not conform. One or more unexpected values. No data sent to the database. Waiting for the next payload.");
}

